import {useState, setState} from 'react';

const UserList = (props) => {

    return ( 

        <div>     
            <h2>READ USERS</h2>
            <select id="select" className="users-list" onChange={(e)=>{

                var index = e.target.selectedIndex;

                console.log(index);

                if(index >= 0) {
                    props.UpdateCurrentUser(props.users[index]);
                }

            }}>
                <option>Select</option>
                {props.users.map((item) => (

                    // console.log(item),
                    // console.log(this),

                    // <option key={item.name} value={item} onClick={props.UpdateCurrentUser.bind(this,item)}>
                    //     {item.name}
                    // </option>

                    <option key={item.name}>
                        {item.name}
                    </option>

                ))}
            </select>
            <ul>
            {props.users.map((item) => (

                // console.log(this),

                    <a key={item.name} value={item} onClick={props.UpdateCurrentUser.bind(this,item)}>
                        {item.name}
                    </a>

                ))}
            </ul>
        </div>
    );

}

export default UserList;
