import React from 'react';
import axios from 'axios';

const DeleteUser = (props) => {

    console.log(props);

    axios.delete('http://localhost:4000/users' + props.user._id, {
        // id: this.refs.id.value
        // name: this.refs.name.value 
    })
    .then((response) => {
        console.log(response);
    })
    .catch((error) => {
        console.log(error);
    });

    window.location.reload(false);

}

const UserSingle = (props) => {

    function test() {
        console.log('http://localhost:4000/users' + props.user._id);
    }

    return ( 
        <div className="">
            <span>ID: {props.user._id}</span>
            <br/>
            <span>NAME: {props.user.name}</span>
            <br/>
            <button className="btn-del" onClick={test}>
                Delete
            </button>
        </div>
    );

}

export default UserSingle;
