    }

    render() {
      const { items } = this.state;

      return (
        <div>
          <div>
            <label>
              First Name :
              <input
                name="fname"
                type="text"
                value={this.state.fname}
                onChange={this.onInputchange}
              />
            </label>
          </div>
          <div>
            <label>
              Last Name :
              <input
                name="lname"
                type="text"
                value={this.state.lname}
                onChange={this.onInputchange}
              />
            </label>
          </div>
          <div>
            <label>
              Email :
              <input
                name="email"
                type="text"
                value={this.state.email}
                onChange={this.onInputchange}
              />
            </label>
          </div>
          <div>
              <button onClick={this.onSubmitForm}>Submit</button>
          </div>
        </div>
      );
    }
  }

  export default SimpleForm;
