import React from 'react';
import axios from 'axios';

const UserSingle = (props) => {

    function DeleteUser() {

        if(props.user._id !== undefined) {
            axios.delete('http://localhost:4000/user/' + props.user._id)
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            });
            window.location.reload(false);
        }

    }

    return ( 
        <section>
          <div className='section-inner'>
            <h2>READ USER</h2>

            <div className='flex'>
                <div>
                    <p>ID:</p>
                    <p>NAME:</p>
                </div>
                <div className='pad-l'>
                    <p>{props.user._id}</p>
                    <p>{props.user.name}</p>
                </div>
            </div>

            <h2>DELETE USER</h2>
            <button className="btn-del del" onClick={DeleteUser}>
                Delete
            </button>
        </div>

        </section>
    );

}

export default UserSingle;
