import React from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from 'axios';
import './App.css';
import UserList from './User/UserList';
import UserSingle from './User/UserSingle';
import CreateUser from './User/CreateUser';
import UpdateUser from './User/UpdateUser';
import Nav from './Nav';


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      Users: [],
      currentUser: {},
    }
    this.UpdateCurrentUser =  this.UpdateCurrentUser.bind(this);
  }

  componentDidMount() {
    const url = 'http://localhost:4000/users';
    axios.get(url)
      .then((Response) => {
        this.setState({
          Users: Response.data
        })
      })
      .catch((error) => {
        console.log(error);
      });
  };

  UpdateCurrentUser(item) {
    this.setState({
      currentUser: item
    })
  }

  render () {
    return (

      <div className="container">

        <Nav />

      {/* <UserList users={this.state.Users} UpdateCurrentUser={this.UpdateCurrentUser} />

      <UserSingle user={this.state.currentUser} />

      <CreateUser />

      <UpdateUser user={this.state.currentUser} /> */}

      <BrowserRouter>
        <Routes>
          <Route path="/" element={ <UserList users={this.state.Users} UpdateCurrentUser={this.UpdateCurrentUser} /> }>

            <Route path="read" element={<UserSingle user={this.state.currentUser} />} />
            <Route path="create" element={<CreateUser />} />
            <Route path="update" element={<UpdateUser user={this.state.currentUser} />} />

          </Route>
        </Routes>
      </BrowserRouter>

      <Router>
        <div>
          <h2>Welcome to React Router Tutorial</h2>
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <ul className="navbar-nav mr-auto">
            <li><Link to={'/'} className="nav-link"> Home </Link></li>
            <li><Link to={'/contact'} className="nav-link">Contact</Link></li>
            <li><Link to={'/about'} className="nav-link">About</Link></li>
          </ul>
          </nav>
          <hr />
          <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
          </Switch>
        </div>
      </Router>


      </div>

    );
  }
}

export default App;
