import React from 'react';
import axios from 'axios';

class UpdateUser extends React.Component {

    submitHandler = e => {
        e.preventDefault();
        var upname = document.getElementById('upname');
        if(this.props.user._id !== undefined) {
            axios.put('http://localhost:4000/user/' + this.props.user._id, {
                name: upname.value
            })
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            });
            window.location.reload(false);
        }
    }

    componentDidUpdate() {
        var upname = document.getElementById('upname');
        upname.value = this.props.user.name || '';
    }

    load = () => {
        console.log(this);
    }

    render() {
        return (
            <section>
                <div onClick={this.load}> load</div>
                <div className='section-inner'>
                    <h2>UPDATE USER</h2>
                    <form onSubmit={this.submitHandler.bind(this)}>
                        <label htmlFor="name">Name</label>
                        <input id="upname" type="text" name="name" />
                        <button className="submit-btn sub" type="submit">Submit</button>
                    </form>
                </div>
            </section>
        )
    }
}

export default UpdateUser;
