import {useState} from 'react';

const UserList = (props) => {

    function handleChange() {
        // console.log('Label ðŸ‘‰ï¸', event.target.selectedOptions[0]);
        // console.log(event.target.value);

        var index = document.getElementById('select').selectedIndex - 1;

        if(index >= 0) {
            console.log(props.users[index]);
            props.UpdateCurrentUser.bind(this,props.users[index]);
        }

    };

    return ( 
        <div>           
            <h2>READ USERS</h2>
            <select id="select" className="users-list" onChange={handleChange} >
                <option>Select</option>
                {props.users.map((item) => (
                    console.log(item),
                    // <option key={item.name} value={item} onClick={props.UpdateCurrentUser.bind(this,item)}>
                    //     {item.name}
                    // </option>

                    <option key={item.name}>
                        {item.name}
                    </option>
                ))}
            </select>
        </div>
    );

}

export default UserList;
