import React from 'react';
import axios from 'axios';
import './App.css';
import UserList from './User/UserList';
import UserSingle from './User/UserSingle';
import CreateUser from './User/CreateUser';
import UpdateUser from './User/UpdateUser';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      Users: [],
      currentUser: {},
      page: ''
    }
    this.UpdateCurrentUser =  this.UpdateCurrentUser.bind(this);
  }

  componentDidMount() {
    const url = 'http://localhost:4000/users';

    axios.get(url)
      .then((Response) => {
        this.setState({
          Users: Response.data
        })
      })
      .catch((error) => {
        console.log(error);
      });
  };

  UpdateCurrentUser(item) {
    this.setState({
      currentUser: item
    })
  }

  state(e) {
    console.log(e);
  }

  render () {
    return (
      <div className="container">

        <nav>
          <h1 className='mar-tb-0'>MERN</h1>
          <p>(MongoDB Express React Node)</p>
        </nav>

        <section id="list" className='page'>
          <div className='section-inner mar-t-0'>
            <UserList 
              users={this.state.Users} 
              UpdateCurrentUser={this.UpdateCurrentUser}
            />
          </div>
        </section>

        <section id="single" className='page'>
          <div className='section-inner'>
            <UserSingle user={this.state.currentUser} />
          </div>
        </section>

        <section id="create" className='page'>
          <div className='section-inner'>
            <CreateUser />2
          </div>
        </section>

        <section id="update" className='page'>
          <div className='section-inner'>
            <UpdateUser user={this.state.currentUser} />
          </div>
        </section>

      </div>
    );
  }
}

export default App;
