import React from 'react';
import axios from 'axios';

const UserSingle = (props) => {

    function DeleteUser() {

        // console.log('http://localhost:4000/users/' + props.user._id);

        axios.delete('http://localhost:4000/user/', {
            _id: props.user._id
        })
        .then((response) => {
            console.log(response);
        })
        .catch((error) => {
            console.log(error);
        });

        // window.location.reload(false);

    }

    return ( 
        <div className="">
            <span>ID: {props.user._id}</span>
            <br/>
            <span>NAME: {props.user.name}</span>
            <br/>
            <button className="btn-del" onClick={DeleteUser}>
                Delete
            </button>
        </div>
    );

}

export default UserSingle;
