        <label>
            Email :
            <input
            name="email"
            type="text"
            value={this.state.email}
            onChange={this.onInputchange}
            />
        </label>
        </div>
        <div>
            <button onClick={this.onSubmitForm}>Submit</button>
        </div>
    </div>
    );
}
}

export default SimpleForm;
