import React from 'react';
import axios from 'axios';

class UpdateUser extends React.Component {

    submitHandler = e => {
        e.preventDefault();

        var upname = document.getElementById('upname');

        // axios.put('http://localhost:4000/users', {
        //     // id: this.refs.id.value,
        //     name: upname.value
        // })
        // .then((response) => {
        //     console.log(response);
        // })
        // .catch((error) => {
        //     console.log(error);
        // });

        if(this.props.user._id !== undefined) {
            axios.put('http://localhost:4000/user/' + this.props.user._id, {
                name: upname.value
            })
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            });

            window.location.reload(false);
        }


        console.log(upname.value);
    }


    // componentDidMount() {
    //     console.log('mount');
    // }
    componentDidUpdate() {
        var upname = document.getElementById('upname');
        upname.value = this.props.user.name || '';
    }

    render() {
        return (
            <div>
                <span>UPDATE USER</span>
                <form onSubmit={this.submitHandler.bind(this)}>
                    <label for="name">NAME</label>
                    <input id="upname" type="text" name="name" />
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}

export default UpdateUser;
