    render() {


    var {name} = this.state;

    if(this.props.user.name !== undefined) {
        // console.log(this.props.user.name);
        name = this.props.user.name;
        // this.componentDidUpdate();
    }

        return (
            <div>
                <form onSubmit={this.submitHandler}>

