import React from 'react';
import axios from 'axios';

const UserSingle = (props) => {

    function DeleteUser() {

        if(props.user._id != undefined) {
            axios.delete('http://localhost:4000/user/' + props.user._id)
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            });

            window.location.reload(false);
        }

    }

    return ( 
        <div className="">
            <span>READ USER</span>
            <br/>
            <span>ID: {props.user._id}</span>
            <br/>
            <span>NAME: {props.user.name}</span>
            <br/>
            <br/>

            <span>DELETE USER</span>
            <br/>
            <button className="btn-del" onClick={DeleteUser}>
                Delete
            </button>
        </div>
    );

}

export default UserSingle;
